        }
    ]
    constructor() {
        super();
        console.log("hello i am news costructor from news component");
        this.state = {
            articles: this.articles,
            loading: false,
            page: 1

        }
    }
    async componentDidMount() {
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=42ddb4056aeb4d4ab37062f04002f663";
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({ articles: parsedData.articles })

    }

    handleNextClick = async () => {
        console.log("hello next  button was clicked ");
        console.log(this.page);

        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=42ddb4056aeb4d4ab37062f04002f663&page=${this.state.page + 1}`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({ articles: parsedData.articles })
        this.setState({
            page: this.state.page + 1,
        })
        console.log(this.page);

    }
    handlePrevClick = async () => {
        console.log("previous button was clicked ");
        console.log(this.page)
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=42ddb4056aeb4d4ab37062f04002f663&page=${this.state.page - 1}`;
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({ articles: parsedData.articles })
        this.setState({
            page: this.state.page - 1,
        })
        console.log(this.page);
    }
    render() {
        return (
            <div className="container my-3">
                <h1>NewsMonkey - TopHeadlines</h1>

                <div className="row">
                    {this.state.articles.map((element) => {
                        return <div className="col-md-4" key={element.url}>
                            <NewsItem title={element.title ? element.title.slice(0, 45) : ""} discription={element.description ? element.description.slice(0, 88) : ""} imageUrl={element.urlToImage} newsUrl={element.url} />
                        </div>
                    })}
                </div>
                <div className="container d-flex justify-content-between">
                    <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevClick} >	&larr; Previous</button>
                    <button disabled={this.state.page >= 2} type="button" className="btn btn-dark" onClick={this.handleNextClick} >Next &rarr;</button>
                </div>

            </div>
        )
    }
}

export default News
